import React, { Component } from 'react';
import countries_data from "./data/dataset-json.json"

export default class NewsCard extends Component{

    constructor(props){
        super(props)
        this.state = {
            country_data: null
        }
    }

    componentDidMount(){
        let country = this.props.country
        let country_data = countries_data.find(s => s.CNTRYNAME == country)
        this.setState({
            country_data: country_data
        })
    }

    componentDidUpdate(){
        let country = this.props.country

        this.setState({
            country_data: country_data
        })
    }


    render(){
        let countryData = countries_data.find(s => s.CNTRYNAME == country)
        if(countryData != null){
            return (
                <div>
                    <h3>{countryData.CNTRYNAME}</h3>
                    <h4>Total Confirmed Cases: {countryData.total}</h4>
                    <table>
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Number of Cases</th>
                                <th>% Change from Previous Day</th>
                            </tr>
                        </thead>
                        <tbody>
                            {countryData.cases_by_date.slice(Math.max(this.state.country_data.cases_by_date.length - 5, 0)).reverse().map(date => {
                                return(
                                    <tr>
                                        <td>{date.date_string}</td>
                                        <td>{date.num_cases}</td>
                                        <td>{date.change.toFixed(2)}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div>
            )
        } else {
            return null
        }
    }

}
