import React, { Component } from 'react';
import countries_data from "./data/dataset-json2.json"

export default class NewsCard extends Component{

    constructor(props){
        super(props)
        this.state = {
            country_data: null
        }
    }

    componentDidMount(){
        let country = "United Arab Emirates"
        let country_data = countries_data.find(s => s.CNTRYNAME == country)
        this.setState({
            country_data: country_data
        })
    }


    render(){
        return (

            <div>
                <h1>Hi</h1>
                <h3>{this.state.country_data.CNTRYNAME}</h3>
                <h4>Total Confirmed Cases: {this.state.country_data.total}</h4>
                <table>
                    <thead>
                        <tr>
                            <th>Date</th>
                            <th>Number of Cases</th>
                            <th>% Change from Previous Day</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.cases_by_date.slice(Math.max(this.state.cases_by_date.length - 5, 0)).map(date => {
                            return(
                                <tr>
                                    <td>{date.date_string}</td>
                                    <td>{date.num_cases}</td>
                                    <td>{date.change}</td>
                                </tr>
                            )
                        })}
                    </tbody>
                </table>
            </div>
        )
    }

}
