import React, { Component } from 'react';
import 'react-dropdown/style.css';
import countries_data from "./Components/data/dataset-json.json"
class Picker extends Component{

    constructor (props) {
        super(props)
        let countryOptions = []

        for(let countryData of countries_data){
        countryOptions.push(countryOptions.CNTRYNAME)
        }

        this.state = {
            countryOptions: countryOptions
        }
    }

    render(){
        return (
            <select 
                required
                className="picker" 
                name="residence" 
                id="residence"
                onChange={this.props.onChange}
            >
                {this.props.options.map(name => {
                    return(
                        <option value={name}>{name}</option>
                    )
                })}
            </select>
        );
    }
}

export default Picker;
