import React, { Component } from 'react';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

class Picker extends Component{

    constructor (props) {
        super(props)
        this._onItemSelected = this._onItemSelected.bind(this)
      }

    _onItemSelected = (option) => {
        if(this.props.cid == 'dest-menu'){
            this.props.onChange(option['label']);
        }
        if(this.props.cid == 'rate-menu'){
            this.props.onChange(option['value']);
        }

    }

    render(){
        return (
            <div className='picker' id={this.props.cid}>
                <Dropdown options={this.props.options} value={this.props.defaultOption} placeholder={this.props.placeholder} onChange={this._onItemSelected.bind(this)}/>
            </div>
        )
    }
}

export default Picker;
