import React, { Component } from 'react';

// b2eddec6445f43e6ad3ff867f25825b5

export default class NewsCard extends Component{

    constructor(props){
        super(props)
        this.state = {
            news_data: null,
            loading: true
        }
    }

    componentDidMount(){
        let country = "United+Arab+Emirates"
        let today = new Date()
        let searchUrl = `http://newsapi.org/v2/everything?q=${country+"+coronavirus"}&from=${today.toISOString().split("T")[0]}&sortBy=publishedAt&apiKey=b2eddec6445f43e6ad3ff867f25825b5`
        fetch(searchUrl)
        .then(res => res.json())
        .then(result => {
            this.setState({
                news_data: result.articles,
                loading: false
            })
        }, error => {
            console.log(error)
        })


    }

    render(){
        return (
            <div>
                {!this.state.loading ?
                    <div className="news-b">
                        {this.state.news_data.map((nd, index) =>{
                            return (
                                <div key={index}>
                                    <h4>{nd.title}</h4>
                                    <img src={nd.urlToImage}/>
                                </div>
                            )
                        })}
                    </div>
                : null}
            </div>
        )
    }
}
