import React, { Component } from 'react';
import logo from './logo.svg';

import MapCard from "./Components/MapCard.jsx"
import NewsCard from "./Components/NewsCard.jsx"
import StatsCard from "./Components/StatsCard.jsx"
import Picker from './Components/Picker';

import './App.css';

class App extends Component {

  constructor(){
    super()
    this.state = {
      currentCountryOption: 'Aruba',
    }
  }

  handleChange = event => {
    // event.preventDefault();
    const el = event.currentTarget;
    if(el.name == "demography"){
        let demos = this.state.demography
        if(el.checked){
            demos.push(el.value)
            // console.log(demos)
        } else {
            const index = demos.indexOf(el.value)
            if (index > -1) {
                demos.splice(index, 1);
            }
            // console.log(demos)
        }
        this.setState({demography: demos})
    } else {
        this.setState({ [el.name]: el.value });
    }

};

  componentDidMount(){

  }

  render(){
    return (
      <div className="App">
        <Picker/>
        <MapCard></MapCard>
        <NewsCard></NewsCard>
        <StatsCard></StatsCard>
      </div>
    );
  }
}

export default App;
