import React, { Component } from 'react';
import { ComposableMap, ZoomableGroup, Geographies, Geography, Markers, Marker, Sphere, Graticule} from "react-simple-maps"
import { scaleLinear } from "d3-scale";
import countries_data from "./data/dataset-json.json"

export default class MapCard extends Component{

    constructor(props){
        super(props);
        this.state = {
            marker: null,
            geoUrl : "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json",
            data: countries_data
        }
    }

    componentDidMount(){
        console.log(this.state.data)
    }

    render(){        
        const colorScale = scaleLinear()
            .domain([0.29, 0.68])
            .range(["#ffedea", "#ff5233"]);

        const wrapperStyles = {
            height: "100%",
            width: "100%",
            margin: "0 auto",
        }

        return (
            <div>
                <ComposableMap
            projectionConfig={{
                rotate: [-10, 0, 0],
                scale: 147
            }}
            >
            <Sphere stroke="#E4E5E6" strokeWidth={0.5} />
            <Graticule stroke="#E4E5E6" strokeWidth={0.5} />
            {data.length > 0 && (
                <Geographies geography={geoUrl}>
                {({ geographies }) =>
                    geographies.map(geo => {
                    const d = data.find(s => s.ISO3 === geo.properties.ISO_A3);
                    return (
                        <Geography
                        key={geo.rsmKey}
                        geography={geo}
                        fill={d ? colorScale(d["2017"]) : "#F5F4F6"}
                        />
                    );
                    })
                }
                </Geographies>
            )}
            </ComposableMap>
            </div>
        )
    }
}
